import React from 'react';
//import axios from 'axios';

class Post extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            id:null
        }
    }
    componentDidMount(){
        let id = this.props.match.params.post.id;
        this.setState({
            id : id
        })
    }
    render(){
        return(
            <div className ="container">
                <h1>{ this.state.id}</h1>
            </div>
        )
    }
}

export default Post
