import React from 'react';
import './App.css';

const Uers = () => {
  const users = [
    { name: 'Robin' },
    { name: 'Markus' },
  ];
  const showUsers = false;
  if (!showUsers) {
    return null;
  }
  return (
    <ul>
      {users.map(user => <li>{user.name}</li>)}
    </ul>
  );
};

class App extends React.Component{
  constructor(props){
    super(props)

    this.state={
      users: ''
    }

    this.setState({
      users:this.state.users
    })
  }
  render(){
    return(
      <div>
        <Users {...this.state.users}/>
      </div>
    )
  }

}

export default App;
