import React from 'react';
import './App.css';

class App{
  constructor(firstname , lastname){
    this.firstname = firstname;
    this.lastname = lastname;

  }
  getname(){
    return ("the full name is :"+this.firstname + this.lastname);
  }
}

var me = new App('dev','smith');
console.log(me.getname());

export default App;
