import React from 'react';
import axios from 'axios';

class Post extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            post : null
        }
    }
    componentDidMount(){
        let id = this.props.match.params.post.id;
        axios.get('https://jsonplaceholder.typicode.com/posts/'+ id)
        .then(response=>{
            this.setState({
                post : response.data
            })
        })
        this.setState({
            id : id
        })
    }
    render(){
        return(
            <div className ="container">
                <h1>{ this.state.id}</h1>
            </div>
        )
    }
}

export default Post
