import React from 'react';
import axios from 'axios';

class Home extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            posts :[]
        }

    }

    componentDidMount(){
        axios.get('https://jsonplaceholder.typicode.com/posts')
        .then((response)=>{
            this.setState({
                posts : response.data.slice(0,10)
            })
        })
    }
    render(){
        const {posts} = this.props;
        const postList = posts.map((post)=>{
            return(<div key = {post.id}>{post}</div>)
        })
        return (
            <div className="container">
                <h4 className="center">Home</h4>
                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Facilis architecto quia ea, laboriosam dolorem quod qui cum rem? Excepturi deserunt, dolores doloribus neque numquam nisi doloremque maiores deleniti recusandae totam!</p>
                    {postList}
            </div>
        )

    }

}

export default Home ;
