import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.state ={
      value :'',
      list_todo : [ ]
    }

  }
  handleChange(e){
    this.setState({
    value:e.target.value
    })
  }

  handleSubmit(e){
  e.preventDefault();
  if(this.state.value.length>1){
    this.setState({
      list_todo : this.state.list_todo.concat(this.state.value),
      value : ''
  })
  }else{
    this.setState({
      list_todo:this.state.list_todo
    })
  }

  }  
  render (){
    return (
      <div className="container">
        <form onSubmit={this.handleSubmit}>
        <label>
          <input className ="input" type="text" value={this.state.value} onChange={this.handleChange} placeholder ="Enter your task here ..."/>
        </label>
        <input className = "submit" type="submit" value="Add" />
      </form>
      <h1>Your todo list is </h1>
      <ul>{this.state.list_todo.map((item)=>
        (<li key ={this.state.item}>{item}</li>))}
      </ul>
      <div className ="exp">
        <h1>To Do List Example</h1>
      </div>  
      </div>
    )
  }
}

export default App;
