import React from 'react';
import './App.css';
import Ninjas from './Ninjas';

class App extends React.Component{
  constructor(props){
    super(props);

    this.handleChange = this.handleChange.bind(this);
    this.state = {
      name :null,
      age: null,
      color:null
    }
  }

  handleChange(e){
      this.setState({
        name : e.target.name,
        age: e.target.age,
        color:e.target.color 
      })
  }

  render(){
    return(
      <div>
        <h1>Welcome</h1>
        <form >
          <input type ="text" name = {this.state.value} onChange ={this.handleChange}/>
          <input type ="text" age = {this.state.value} onChange ={this.handleChange}/>
          <input type ="text" color = {this.state.value} onChange ={this.handleChange}/>
          <button type ="submit">Submit</button>
        </form>
        <Ninjas user = {this.state.name} />
      </div>
    )
  }
}



export default App;
