import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);

    this.handleChange = this.handleChange.bind(this);
    this.state = {
      name :''
    }
  }

  handleChange(e){
      this.setState({
        name : e.target.value
      })
      console.log(name)
  }

  render(){
    return(
      <div>
        <h1>Hello User :)</h1>
        <form >
          <input type ="text" value ={this.state.value} onChange ={this.handleChange}/>
          <button type ="submit">Submit</button>
        </form>
      </div>
    )
  }
}



export default App;
