import React from 'react';
//import './App.css';
import Ninjas from './Ninjas';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state ={
      ninjas :[
        {name :"Ryu" ,age :"25" ,belt: "Black"},
        {name :"Crystal" ,age :"20" ,belt: "Green"},
        {name :"Ryu" ,age :"30" ,belt: "Gold"}
      ]
    }
  }

  render(){
    return(
      <div>
        <h1>Hello Ninjas</h1>
        <p>Please add the new ninja</p>
        <Ninjas ninjas = {this.state.ninjas} />
      </div>
    )
  }
}


export default App;
